/* 
    0 , 1の配列を8個区切りの1バイトにして、それらを合わせてBufferにして返す
*/
function bin2sendBuffer(data){
  // シリアルそ送信する文字列
  var array = [];

  // 8個区切りで0, 1の配列を読み取る 
  for(var i = 0; i < data.length; i+= 8){
    // 8bitを整数値にする（nに整数値が入る）
    var n = 0;
    for(var j = 0; j < 8; j++){
      n <<= 1;
      if(i+j < data.length) n |= data[i+j];
    }
    array.push(n);
  }
  return new Buffer(array);
}


